	// Function that prevents the addition of too many items in a cart.
	function wpbooklistCustomAutoFillAddToCartCheck() {

		if ( -1 < window.location.href.indexOf( '/shop/' ) ) {

			$( document ).on( 'change', 'input[name="quantity"]', function( event ) {
				if ( ( parseInt( $(this).val() ) ) >= parseInt( $(this).attr('max') ) ) {
					$(this).val( $(this).attr('max') );
				}
			});

		}

	}


